package com.example.currencyconverter.data.cache

import android.graphics.BitmapFactory
import android.util.Base64
import androidx.compose.ui.graphics.ImageBitmap
import androidx.compose.ui.graphics.asImageBitmap
import com.example.currencyconverter.data.model.CurrencyData

object FlagCache{
    const val ERROR_FLAG = "data:image/png;base64,"
    const val EU_FLAG = "data:image/png;base64,"

    // a map for storing already decoded images
    val flagBitmaps: MutableMap<String, ImageBitmap?> = mutableMapOf(
        "error" to decodeBase64ToBitmap(ERROR_FLAG)
    )
    // try
    fun getImageBitmap(currencyData: CurrencyData): ImageBitmap {
        val code = currencyData.description?.code ?: "error"
        if (!flagBitmaps.containsKey(code) || flagBitmaps[code] == null){
            val bitmap = decodeBase64ToBitmap(currencyData.flagUrl) ?: decodeBase64ToBitmap(ERROR_FLAG)
            flagBitmaps.put(code, bitmap)
        }

        return flagBitmaps[code]!!
    }
    // Helper function to decode Base64
    private fun decodeBase64ToBitmap(base64String: String?): ImageBitmap? {
        return try {
            val pureBase64Encoded = base64String?.substringAfter("base64,")
            val decodedBytes = Base64.decode(pureBase64Encoded, Base64.DEFAULT)
            val bitmap = BitmapFactory.decodeByteArray(decodedBytes, 0, decodedBytes.size)
            bitmap.asImageBitmap()
        } catch (e: Exception) {
            e.printStackTrace()
            null
        }
    }
}
